DELIMITER $$

CREATE TRIGGER `lastu` BEFORE UPDATE ON `translations` FOR EACH ROW BEGIN
SET NEW.`lastupdate` = UNIX_TIMESTAMP();
END$$


DELIMITER ;

/* Procedure structure for procedure `addLocaleToTranslation` */

drop procedure if exists `addLocaleToTranslation`;

DELIMITER $$

CREATE DEFINER=`root`@`localhost` PROCEDURE `addLocaleToTranslation`(IN tid INT, IN loc VARCHAR(255))
BEGIN
UPDATE `translations` SET `locale` = loc WHERE `id`= tid;
END$$

DELIMITER ;

/* Procedure structure for procedure `addNewTranslation` */

drop procedure if exists `addNewTranslation`;

DELIMITER $$

CREATE DEFINER=`root`@`localhost` PROCEDURE `addNewTranslation`(IN loc VARCHAR(255), IN str VARCHAR(255), IN ns VARCHAR(255), IN trans TEXT)
BEGIN
INSERT INTO translations VALUES (NULL, loc, ns, str, trans, UNIX_TIMESTAMP());
END$$

DELIMITER ;

/* Procedure structure for procedure `addTranslation` */

drop procedure if exists `addTranslation`;

DELIMITER $$

CREATE DEFINER=`root`@`localhost` PROCEDURE `addTranslation`(IN loc VARCHAR(255), IN str VARCHAR(255), IN ns VARCHAR(255), IN trans TEXT)
BEGIN
DECLARE done INT DEFAULT 0;
DECLARE tid INT DEFAULT 0;
DECLARE t_locales VARCHAR(255);
DECLARE entrycheck CURSOR FOR SELECT `id`, `locale` from `translations` WHERE `string` = str AND `namespace` = ns AND `translation` = trans ORDER BY `id` ASC LIMIT 1;
DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;
OPEN entrycheck;
REPEAT
FETCH entrycheck INTO tid, t_locales;
UNTIL done END REPEAT;
CLOSE entrycheck;
IF tid > 0 THEN
SET loc = CONCAT(loc, ',', t_locales);
CALL addLocaleToTranslation(tid, loc);
ELSE
CALL addNewTranslation(loc, str, ns, trans);
END IF;
END$$

DELIMITER ;

