/* Procedure structure for procedure `getAllTranslations` */

drop procedure if exists `getAllTranslations`;

DELIMITER $$

CREATE DEFINER=`root`@`localhost` PROCEDURE `getAllTranslations`(IN loc VARCHAR(5), IN ns VARCHAR(255))
BEGIN
